import React, { Component } from 'react'
import './CSS/todo.css'



class Form extends Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: "",
            lastName: "",
            Country: "",
            State  : "",
            City: "",
            Dateofbirth: "",
            gender: "",


        }
        this.handleSubmit=this.handleSubmit.bind(this)
    }

    firsthandler = (event) => {
        this.setState({
            firstName: event.target.value
        })
    }
    lasthandler = (event) => {
        this.setState({
            lastName: event.target.value
        })
    }
   /* passwordhandler = (event) => {
        this.setState({
            password: event.target.value
        })
    }
     */
    genderhandler = (event) => {
        this.setState({
            gender: event.target.value
        })
    }

    countryhandler = (event) => {
        this.setState({
            country: event.target.value
        })
    }

    statehandler = (event) => {
        this.setState({
            state: event.target.value
        })
    }

    cityhandler = (event) => {
        this.setState({
            city: event.target.value
        })
    }

    handleSubmit = (event) => {
        alert(`${this.state.firstName} ${this.state.lastName}  Registered Successfully !!!!`)
        console.log(this.state);
        this.setState({
            firstName: "",
            lastName: "",
            Country: "",
            State  : "",
            City: "",
            Dateofbirth: "",
            gender: "",
        })
     event.preventDefault()

    }




    render() {
        return (
            <div>

                <form onSubmit={this.handleSubmit}>
                    <h1>User Registration</h1>
                    <label>FirstName :</label> <input type="text" value={this.state.firstName} onChange={this.firsthandler} placeholder="FirstName..." /><br />
                    <label>LastName :</label> <input type="text" value={this.state.lastName} onChange={this.lasthandler} placeholder="LastName..." /><br />
                    <label>Country :</label><select onChange={this.countryhandler} defaultValue="Select Country">
                        <option defaultValue>Select Country</option>
                        <option value="india">India</option>
                        <option value="australia">Australia</option>
                    </select><br />
                    <label>State :</label><select onChange={this.statehandler} defaultValue="Select State">
                        <option defaultValue>Select State</option>
                        <option value="maharashtra">Maharashtra</option>
                        <option value="delhi">Delhi</option>
                    </select><br />
                    <label>City :</label><select onChange={this.cityhandler} defaultValue="Select City">
                        <option defaultValue>Select City</option>
                        <option value="ahmednagar">Ahmednagar</option>
                        <option value="pune">Pune</option>
                    </select><br />

                    <label>Gender :</label><select onChange={this.genderhandler} defaultValue="Select Gender">
                        <option defaultValue>Select Gender</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                    </select><br />
                    <input type="submit" value="Submit" />
                </form>

            </div>

        )
    }
}

export default Form
